pragma solidity >=0.4.0 < 0.6.0;

contract giving{
    
    //Create a variable for each possible address

    mapping (address=>bool) private ownership;
    //Save the owner address 
    address master;
    //The event to control iot devices
    event openDoor();
    //The event to transfer permission
    event okToGive(address to);
    
    constructor() public{
        master = msg.sender;
        ownership[msg.sender] = true;
    }
    function checkPermission(address target) public{
        require(ownership[target]==true, "You don't have permission");
        emit openDoor();
    }
    function trans(address target) public{
        require(msg.sender==master, "Only master can give permission!");
        ownership[target] = true;
        emit okToGive(target);
    }
}
